public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//şart blockları (Conditionals)
		int sayi = 20;
		
		if(sayi < 20) {
			System.out.println("sayi 20'den küçüktür");
		}else {
			System.out.println("sayi 20'den büyük veya 20ye eşit");
		}
	}

}
